// Configurazione per scrivere dati su InfluxDB
// Supporta sia connessione locale che tramite ngrok

const INFLUX_CONFIG = {
    // URL locale (all'interno di Docker)
    local: 'http://influxdb:8086',

    // URL pubblico tramite ngrok (per test esterni)
    public: 'https://more-elk-slightly.ngrok-free.app',

    // Credenziali
    token: 'svt-super-secret-token-123456789',
    org: 'SVT-Vicenza',
    bucket: 'bus-data',

    // Headers comuni
    headers: {
        'Authorization': 'Token svt-super-secret-token-123456789',
        'Content-Type': 'text/plain; charset=utf-8'
    }
};

// Usa URL locale per Node-RED (pi√π veloce)
const baseUrl = INFLUX_CONFIG.local;

// Costruisci URL completo per la scrittura
const writeUrl = `${baseUrl}/api/v2/write?org=${INFLUX_CONFIG.org}&bucket=${INFLUX_CONFIG.bucket}&precision=ns`;

// Imposta configurazione nel messaggio
msg.url = writeUrl;
msg.headers = INFLUX_CONFIG.headers;

// Salva configurazione nel flow context per riutilizzo
flow.set('influx_config', INFLUX_CONFIG);

return msg;
